mapper.select:
select org.path_name, yyy.group_name, yyy.crt_name, yyy.is_cross, to_char(yyy.msg_count) msg_count from (select t.group_name, u.name as crt_name, u.org_name, u.org_code, t.iscross is_cross, nvl(m.msgcount, 0) msg_count from zz_group t, lark_admin.admin_user u, (select count(1) as msgcount, receiver from zz_message_info where type = 'GROUP' group by receiver) m where u.id = t.creator and t.group_id = m.receiver(+)) yyy, lark_admin.admin_org org where org.id = yyy.org_code
and group_name like '%'||#{groupName}||'%'
and is_cross = #{isCross}

select to_char(file_count) file_count, to_char(pirivate_file_count) pirivate_file_count, to_char(group_file_count) group_file_count, to_char(meet_file_count) meet_file_count, to_char(text_file_count) text_file_count, to_char(zip_file_count) zip_file_count, to_char(pic_file_count) pic_file_count, to_char(other_file_count) other_file_count
from (select 'file_count' as title, count(1) as file_count from zz_group_file where 1 = 1 union select 'pirivate_file_count' as title, count(1) as file_count from zz_group_file where is_group = '0' union select 'group_file_count' as title, count(1) as file_count from zz_group_file where is_group = '1' union select 'meet_file_count' as title, count(1) as file_count from zz_group_file where is_group = '905' union select 'text_file_count' as title, count(1) as file_count from zz_group_file where (file_type = 'text' or file_type = 'doc') union select 'zip_file_count' as title, count(1) as file_count from zz_group_file where file_type = 'zip' union select 'pic_file_count' as title, count(1) as file_count from zz_group_file where file_type = 'img' union select 'other_file_count' as title, count(1) as file_count from zz_group_file where file_type = 'other') a pivot(max(file_count) for title in('file_count' as file_count, 'pirivate_file_count' as pirivate_file_count, 'group_file_count' as group_file_count, 'meet_file_count' as meet_file_count, 'text_file_count' as text_file_count, 'zip_file_count' as zip_file_count, 'pic_file_count' as pic_file_count, 'other_file_count' as other_file_count)
) where 1 = 1
